    private void addStudentButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addStudentButtonActionPerformed
       
        //Creating a child from the parent class "studentInfo.java"
        //Polymorphism
        Person p = new studentInfo();
        studentInfo s = new studentInfo();
        
        //useing setters and getters to handle Encapsulated data parameters
        String studentName= fullNameField.getText();
        s.setName(fullNameField.getText());
        
        String studentDateOfBirth = dateOfBirthField.getText();
        s.setDateOfBirth(dateOfBirthField.getText());
        
        String studentEmail = emailfield.getText();
        s.setEmail(emailfield.getText());
       
        String studentNationalId = nationalIdField.getText();
        s.setNationalID(nationalIdField.getText());
        
        String studentAddress = addressField.getText();
        s.setAddress(addressField.getText());
        
        String studentPhone = phoneField.getText();
        s.setPhone(phoneField.getText());
        
        String studentGrade = gradeField.getText();
        s.setGrade(Double.parseDouble(gradeField.getText()));
        
        
        String studentPassword = passwordField.getText();
        s.setPassword(passwordField.getText());
        
        //Making sure data fields isn't empty or invalid
         if (studentName.isEmpty() || studentDateOfBirth.isEmpty()
                 || studentNationalId.isEmpty()|| studentAddress.isEmpty()||
                 studentEmail.isEmpty()|| studentPhone.isEmpty()|| studentGrade.isEmpty() ) {
             
            JOptionPane.showMessageDialog(null, "please enter all the information");
            
        }else{  if(!studentEmail.endsWith("@gmail.com")){
                 JOptionPane.showMessageDialog(null, "please enter A valid Email");
             }else{
             
             DatabaseConnection.addStudent(s.getName(), s.getDateOfBirth(), s.getEmail(),
                     s.getNationalID(), s.getAddress(), s.getPhone(), s.getGrade(), s.getPassword());
                  
         
            
            //setting data fields to defult after useage
            fullNameField.setText("");
            dateOfBirthField.setText("");
            nationalIdField.setText("");
            addressField.setText("");
            emailfield.setText("");
            phoneField.setText("");
            gradeField.setText("");
            passwordField.setText("");
            
       }}
        
    }//GEN-LAST:event_addStudentButtonActionPerformed

