    private void addTeacherButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addTeacherButtonActionPerformed
        //Creating a child from the parent class "teacherInfo.java"
        //Polymorphism
        Person p = new teacherInfo();
        teacherInfo t = new teacherInfo();
        
        //useing setters and getters to handle Encapsulated data parameters
        String teacherName = teacherNameField.getText();
        t.setName(teacherNameField.getText());
        
        String taecherAge  = teacherAgeField.getText();
        t.setAge(Integer.parseInt(teacherAgeField.getText()));
        
        String teacherSubject = teacherSubjectField.getText();
        t.setSubject(teacherSubjectField.getText());
        
        String teacherSalary= teacherSalaryField.getText();
        t.setSalary(Double.parseDouble(teacherSalaryField.getText()));
        
        String teacherPassword= teacherPasswordField.getText();
        t.setPassword(teacherPasswordField.getText());
        
        //Making sure data fields isn't empty or invalid
        if (teacherName.isEmpty() || taecherAge.isEmpty() || teacherSubject.isEmpty() || teacherSalary.isEmpty() 
                ||teacherPassword.isEmpty() ) {
             
            JOptionPane.showMessageDialog(null, "please enter all the information");
            
        }else{ 
            //database connection 
            DatabaseConnection.addTeacher(t.getName() , t.getAge(), t.getSubject()
                    , t.getSalary(), t.getPassword());
            
            //setting data fields to defult after useage
            teacherNameField.setText("");
            teacherAgeField.setText("");
            teacherSubjectField.setText("");
            teacherSalaryField.setText("");
            teacherPasswordField.setText("");
            
        }
        
    }//GEN-LAST:event_addTeacherButtonActionPerformed
